// src/components/Card.js
import React, { forwardRef } from 'react'; // <--- Import forwardRef
import { useDrag } from 'react-dnd';

const ItemTypes = {
  CARD: 'card',
};

// Use forwardRef to allow parent components (like Board) to get a ref to this component's DOM node
const Card = forwardRef(({ id, text, laneId, index }, ref) => { // <--- Add forwardRef and ref prop
  const [{ isDragging }, drag] = useDrag(() => ({
    type: ItemTypes.CARD,
    item: { id, text, laneId, index },
    collect: (monitor) => ({
      isDragging: monitor.isDragging(),
    }),
  }));

  const opacity = isDragging ? 0.4 : 1;

  return (
    <div
      ref={el => {
        drag(el); // Assign to react-dnd's drag ref
        if (ref) ref.current = el; // <--- Assign to the ref passed from parent
      }}
      className="bg-white p-4 mb-3 rounded shadow-md cursor-grab transition-opacity duration-200 ease-in-out"
      style={{ opacity }}
    >
      {text}
    </div>
  );
});

export default Card;
export { ItemTypes };
