// src/components/Board.js
import React, { useState, useCallback, useRef, useEffect } from 'react';
import Lane from './Lane';
import Card from './Card';
import FlyingCardAnimation from './FlyingCardAnimation';

const Board = () => {
  const [lanes, setLanes] = useState([
    { id: 'lane-1', title: 'To Do', cards: [{ id: 'card-1', text: 'Define project scope' }, { id: 'card-2', text: 'Research market trends' }] },
    { id: 'lane-2', title: 'In Progress', cards: [{ id: 'card-3', text: 'Develop initial prototype' }] },
    { id: 'lane-3', title: 'Done', cards: [{ id: 'card-4', text: 'Brainstorming session' }] },
  ]);

  const [flyingAnimations, setFlyingAnimations] = useState([]);

  const cardRefs = useRef(new Map());
  const laneRefs = useRef(new Map());

  const [isReady, setIsReady] = useState(false);

  useEffect(() => {
    const timeoutId = setTimeout(() => {
      setIsReady(true);
      console.log('Board component and initial refs are likely ready.');
      console.log('Current cardRefs size (after delay):', cardRefs.current.size);
      console.log('Current laneRefs size (after delay):', laneRefs.current.size);
    }, 100);

    return () => clearTimeout(timeoutId);
  }, []);

  const getCardById = useCallback((cardId) => {
    for (const lane of lanes) {
      const card = lane.cards.find(c => c.id === cardId);
      if (card) return card;
    }
    return null;
  }, [lanes]);

  const moveCard = useCallback((cardId, currentLaneId, targetLaneId) => {
    setLanes((prevLanes) => {
      const newLanes = prevLanes.map((lane) => ({ ...lane, cards: [...lane.cards] }));
      let movedCard = null;
      const sourceLaneIndex = newLanes.findIndex((lane) => {
          const cardIndex = lane.cards.findIndex((card) => card.id === cardId);
          if (cardIndex > -1) {
              movedCard = lane.cards.splice(cardIndex, 1)[0];
              return true;
          }
          return false;
      });

      if (movedCard) {
        const targetLaneIndex = newLanes.findIndex((lane) => lane.id === targetLaneId);
        if (targetLaneIndex > -1) {
          newLanes[targetLaneIndex].cards.push(movedCard);
        }
      }
      return newLanes;
    });
  }, []);

  const programmaticMoveCard = useCallback((cardId, targetLaneId) => {
    console.log('--- Clicking Auto Move Button ---');
    console.log('Attempting to move card:', cardId, 'to lane:', targetLaneId);
    console.log('Current cardRefs map (at click):', cardRefs.current);
    console.log('Current laneRefs map (at click):', laneRefs.current);

    const cardElement = cardRefs.current.get(cardId);
    const targetLaneElement = laneRefs.current.get(targetLaneId);

    console.log('Resolved Card Element (at click):', cardElement);
    console.log('Resolved Target Lane Element (at click):', targetLaneElement);

    if (!cardElement || !targetLaneElement) {
      console.warn(`Refs for card ${cardId} or lane ${targetLaneId} not ready yet. Try again in a moment.`);
      return;
    }

    const startRect = cardElement.getBoundingClientRect();
    const currentCardData = getCardById(cardId);

    setFlyingAnimations(prev => [
      ...prev,
      {
        id: `flying-${cardId}-${Date.now()}`,
        card: currentCardData,
        startRect: startRect,
      }
    ]);

    setLanes((prevLanes) => {
      const newLanes = prevLanes.map((lane) => ({ ...lane, cards: [...lane.cards] }));
      let movedCard = null;
      const sourceLaneIndex = newLanes.findIndex((lane) => {
          const cardIndex = lane.cards.findIndex((card) => card.id === cardId);
          if (cardIndex > -1) {
              movedCard = lane.cards.splice(cardIndex, 1)[0];
              return true;
          }
          return false;
      });

      if (movedCard) {
        const targetLaneIndex = newLanes.findIndex((lane) => lane.id === targetLaneId);
        if (targetLaneIndex > -1) {
          newLanes[targetLaneIndex].cards.push(movedCard);
        }
      }
      return newLanes;
    });

    requestAnimationFrame(() => {
      const endCardElement = cardRefs.current.get(cardId);
      if (endCardElement) {
        const endRect = endCardElement.getBoundingClientRect();
        setFlyingAnimations(prev => prev.map(anim =>
          anim.card.id === cardId && !anim.endRect
            ? { ...anim, endRect: endRect }
            : anim
        ));
      } else {
        setFlyingAnimations(prev => prev.filter(anim => anim.card.id !== cardId));
      }
    });

  }, [getCardById]);

  const handleAnimationEnd = useCallback((cardId) => {
    setFlyingAnimations(prev => prev.filter(anim => anim.card.id !== cardId));
  }, []);

  return (
    <div className="flex p-8 overflow-x-auto min-h-screen items-start bg-gray-50">
      <button
        onClick={() => programmaticMoveCard('card-1', 'lane-2')}
        disabled={!isReady}
        className={`fixed bottom-4 right-4 text-white font-bold py-2 px-4 rounded shadow-lg z-50 ${
          isReady ? 'bg-blue-500 hover:bg-blue-700' : 'bg-gray-400 cursor-not-allowed'
        }`}
      >
        Auto Move Card 1 to 'In Progress' (Gliding)
      </button>

      {lanes.map((lane) => (
        <div
          key={lane.id}
          ref={el => {
            if (el) {
              laneRefs.current.set(lane.id, el);
              console.log(`SETTING LANE REF for ${lane.id}. Current size: ${laneRefs.current.size}`); // Debug
            } else {
              laneRefs.current.delete(lane.id);
              console.log(`DELETING LANE REF for ${lane.id}. Current size: ${laneRefs.current.size}`); // Debug
            }
          }}
          className="w-80 p-4 rounded-lg shadow-inner flex-shrink-0 mr-4 bg-gray-100"
        >
          <h2 className="text-xl font-semibold mb-4 text-gray-800 border-b border-gray-300 pb-2">{lane.title}</h2>
          {lane.cards.map((card, index) => {
            const isFlyingClone = flyingAnimations.some(anim => anim.card.id === card.id && anim.endRect);
            return (
              <Card
                key={card.id}
                id={card.id}
                text={card.text}
                laneId={lane.id}
                index={index}
                ref={el => {
                  if (el) {
                    cardRefs.current.set(card.id, el);
                    console.log(`SETTING CARD REF for ${card.id}. Current size: ${cardRefs.current.size}`); // Debug
                  } else {
                    cardRefs.current.delete(card.id);
                    console.log(`DELETING CARD REF for ${card.id}. Current size: ${cardRefs.current.size}`); // Debug
                  }
                }}
                style={{ visibility: isFlyingClone ? 'hidden' : 'visible' }}
              />
            );
          })}
        </div>
      ))}

      {flyingAnimations.map(anim => anim.startRect && anim.endRect && (
        <FlyingCardAnimation
          key={anim.id}
          card={anim.card}
          startRect={anim.startRect}
          endRect={anim.endRect}
          onAnimationEnd={handleAnimationEnd}
        />
      ))}
    </div>
  );
};

export default Board;
