// kanban-gui-test/src/components/FlyingCardAnimation.js

import React, { useEffect, useRef, useState } from 'react';

const FlyingCardAnimation = ({ card, startRect, endRect, onAnimationEnd }) => {
  const animatedRef = useRef(null);
  const animationInstanceRef = useRef(null);

  useEffect(() => {
    // Only proceed if DOM element is mounted and we have both start and end positions.
    // The component renders initially with just startRect, waiting for endRect.
    if (!animatedRef.current || !startRect || !endRect) {
        return;
    }

    // Ensure animation is only started once for this component instance
    if (animationInstanceRef.current) {
        return;
    }

    console.log(`[FlyingCardAnimation] Karte ${card.id}: Starting animation from (${startRect.left},${startRect.top}) to (${endRect.left},${endRect.top})`);

    const deltaX = endRect.left - startRect.left;
    const deltaY = endRect.top - startRect.top;

    const keyframes = [
      { transform: 'translate(0, 0)', opacity: 1 },
      { transform: `translate(${deltaX}px, ${deltaY}px)`, opacity: 1 },
    ];

    const options = {
      duration: 500, // Animation duration in milliseconds
      easing: 'ease-in-out',
      fill: 'forwards',
    };

    animationInstanceRef.current = animatedRef.current.animate(keyframes, options);

    // Use the Promise-based `finished` property for more reliable completion detection.
    animationInstanceRef.current.finished.then(() => {
      console.log(`[FlyingCardAnimation] Promise-based animation finished for Karte: ${card.id}`);
      onAnimationEnd(card.id); // Call the parent's handler to remove this animation entry
    }).catch(error => {
      if (error.name === 'AbortError') {
        console.log(`[FlyingCardAnimation] Karte ${card.id}: Animation was cancelled.`);
      } else {
        console.error(`[FlyingCardAnimation] Karte ${card.id}: Animation error:`, error);
      }
    });

    // Fallback: If for some reason onfinish promise doesn't resolve,
    // ensure the animation entry is cleared after a set time.
    const fallbackTimeout = setTimeout(() => {
        if (animationInstanceRef.current && animationInstanceRef.current.playState !== 'finished' && animationInstanceRef.current.playState !== 'idle') {
            console.warn(`[FlyingCardAnimation] Fallback: Animation for Karte ${card.id} did not finish (playState: ${animationInstanceRef.current.playState}). Forcing cleanup.`);
            onAnimationEnd(card.id);
            animationInstanceRef.current.cancel(); // Cancel to clean up DOM elements potentially held by animation
        }
    }, options.duration + 200); // 200ms grace period after expected duration

    return () => {
      if (animationInstanceRef.current) {
        animationInstanceRef.current.cancel(); // Clean up animation on unmount
      }
      clearTimeout(fallbackTimeout); // Clear fallback if component unmounts early or animation finishes properly
    };
  }, [card.id, startRect, endRect, onAnimationEnd]); // Dependencies: re-run if these values change

  return (
    <div
      ref={animatedRef}
      className="bg-white p-4 mb-3 rounded shadow-md pointer-events-none"
      style={{
        position: 'fixed',
        left: startRect ? startRect.left : 0,
        top: startRect ? startRect.top : 0,
        width: startRect ? startRect.width : 0,
        height: startRect ? startRect.height : 0,
        zIndex: 1000,
        opacity: startRect ? 1 : 0,
      }}
    >
      {/* Ensure "Karte" text for gliding clone */}
      {card.birth_id ? `Karte ${card.birth_id}` : card.id}
    </div>
  );
};

export default FlyingCardAnimation;
