// src/components/Board.js
import React, { useState, useCallback } from 'react';
import Lane from './Lane';

const Board = () => {
  const [lanes, setLanes] = useState([
    {
      id: 'lane-1',
      title: 'To Do',
      cards: [
        { id: 'card-1', text: 'Define project scope' },
        { id: 'card-2', text: 'Research market trends' },
      ],
    },
    {
      id: 'lane-2',
      title: 'In Progress',
      cards: [
        { id: 'card-3', text: 'Develop initial prototype' },
      ],
    },
    {
      id: 'lane-3',
      title: 'Done',
      cards: [
        { id: 'card-4', text: 'Brainstorming session' },
      ],
    },
  ]);

  // Existing: Function to handle card moves via drag-and-drop
  const moveCard = useCallback((cardId, currentLaneId, targetLaneId) => {
    setLanes((prevLanes) => {
      const newLanes = prevLanes.map((lane) => ({ ...lane, cards: [...lane.cards] }));

      // Find the card to move from its current lane
      let movedCard = null;
      const sourceLaneIndex = newLanes.findIndex((lane) => lane.id === currentLaneId);
      if (sourceLaneIndex > -1) {
        const cardIndex = newLanes[sourceLaneIndex].cards.findIndex((card) => card.id === cardId);
        if (cardIndex > -1) {
          movedCard = newLanes[sourceLaneIndex].cards.splice(cardIndex, 1)[0];
        }
      }

      // If card found and removed, add the card to the target lane
      if (movedCard) {
        const targetLaneIndex = newLanes.findIndex((lane) => lane.id === targetLaneId);
        if (targetLaneIndex > -1) {
          newLanes[targetLaneIndex].cards.push(movedCard);
        }
      }

      return newLanes;
    });
  }, []);

  // NEW: Function to programmatically move a card
  const programmaticMoveCard = useCallback((cardId, targetLaneId) => {
    setLanes((prevLanes) => {
      const newLanes = prevLanes.map((lane) => ({ ...lane, cards: [...lane.cards] }));

      let movedCard = null;
      let sourceLaneIndex = -1;

      // Find the card and its current lane
      for (let i = 0; i < newLanes.length; i++) {
        const cardIndex = newLanes[i].cards.findIndex((card) => card.id === cardId);
        if (cardIndex > -1) {
          movedCard = newLanes[i].cards.splice(cardIndex, 1)[0]; // Remove card from source lane
          sourceLaneIndex = i;
          break; // Card found and removed, exit loop
        }
      }

      // If card was found and removed, add it to the target lane
      if (movedCard) {
        const targetLaneIndex = newLanes.findIndex((lane) => lane.id === targetLaneId);
        if (targetLaneIndex > -1) {
          newLanes[targetLaneIndex].cards.push(movedCard); // Add card to target lane
        }
      }
      return newLanes;
    });
  }, []);

  return (
    <div className="flex p-8 overflow-x-auto min-h-screen items-start bg-gray-50">
      {/* NEW: Demo button for automatic movement */}
      <button
        onClick={() => programmaticMoveCard('card-1', 'lane-2')} // Moves 'card-1' (Define project scope) to 'lane-2' (In Progress)
        className="fixed bottom-4 right-4 bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded shadow-lg z-50"
      >
        Auto Move Card 1 to 'In Progress'
      </button>

      {lanes.map((lane) => (
        <Lane key={lane.id} id={lane.id} title={lane.title} cards={lane.cards} moveCard={moveCard} />
      ))}
    </div>
  );
};

export default Board;
