// src/components/Board.js
import React, { useState, useCallback, useRef, useEffect } from 'react';
import Lane from './Lane';
import Card from './Card';
import FlyingCardAnimation from './FlyingCardAnimation';

const API_BASE_URL = 'http://127.0.0.1:8000';

const Board = () => {
  const [lanes, setLanes] = useState([]);
  const [dashboardData, setDashboardData] = useState([]);

  // NEW: complexity is now a dictionary
  const [complexity, setComplexity] = useState({ 1: 1, 3: 1, 5: 1 }); // Default for "Schritt A/B/C"
  const [wipLimit, setWipLimit] = useState(5);
  const [simSpeed, setSimSpeed] = useState(1.0);

  const [simulationRunning, setSimulationRunning] = useState(false);

  const flyingAnimations = useRef([]); // Ref for mutable array of animations
  const cardRefs = useRef(new Map());
  const laneRefs = useRef(new Map());

  const [isReady, setIsReady] = useState(false);
  useEffect(() => {
    const timeoutId = setTimeout(() => {
      setIsReady(true);
      console.log('Board component and initial refs are likely ready.');
    }, 100);
    return () => clearTimeout(timeoutId);
  }, []);

  const getCardById = useCallback((cardId) => {
    for (const lane of lanes) {
      const card = lane.cards.find(c => c.id === cardId);
      if (card) return card;
    }
    return null;
  }, [lanes]);

  const programmaticMoveCard = useCallback((cardId, targetLaneId) => {
    const cardElement = cardRefs.current.get(cardId);
    const targetLaneElement = laneRefs.current.get(targetLaneId);

    if (!cardElement || !targetLaneElement) {
      console.warn(`[Gliding] Could not find element for card ${cardId} or lane ${targetLaneId}. Cannot animate.`);
      return;
    }

    const startRect = cardElement.getBoundingClientRect();
    const currentCardData = getCardById(cardId);

    if (!currentCardData) {
        console.warn(`[Gliding] Card ${cardId} not found in current state for animation.`);
        return;
    }

    // Add animation data immediately
    flyingAnimations.current.push({
        id: `flying-${cardId}-${Date.now()}`,
        card: currentCardData,
        startRect: startRect,
        endRect: null // Will be updated via rAF
    });
    // Trigger re-render to include the new FlyingCardAnimation component
    flyingAnimations.current = [...flyingAnimations.current]; // Force re-render if using useRef directly
    // Or, better, use a useState for flyingAnimations if not already. Let's use useState as initially planned for better React management.
    // Revert flyingAnimations to useState if it's useRef in your current code:
    // const [flyingAnimations, setFlyingAnimations] = useState([]);

    // Schedule getting endRect after React re-renders the actual card
    requestAnimationFrame(() => {
      const endCardElement = cardRefs.current.get(cardId);
      if (endCardElement) {
        const endRect = endCardElement.getBoundingClientRect();
        // Find and update the specific animation entry
        const indexToUpdate = flyingAnimations.current.findIndex(anim => anim.card.id === cardId && anim.endRect === null);
        if (indexToUpdate !== -1) {
            flyingAnimations.current[indexToUpdate].endRect = endRect;
            flyingAnimations.current = [...flyingAnimations.current]; // Force re-render
        }
      } else {
        console.warn(`[Gliding] Card ${cardId} not found in new state to determine endRect.`);
        // Remove animation if endRect cannot be found
        flyingAnimations.current = flyingAnimations.current.filter(anim => anim.card.id !== cardId);
      }
    });

  }, [getCardById]);


  const handleAnimationEnd = useCallback((animatedCardId) => {
    console.log(`[Gliding] Animation ended for card: ${animatedCardId}. Clearing animation entry.`);
    flyingAnimations.current = flyingAnimations.current.filter(anim => anim.card.id !== animatedCardId);
    flyingAnimations.current = [...flyingAnimations.current]; // Force re-render
  }, []);

  const setConfig = useCallback(async () => {
    console.log("setConfig: Attempting to set simulation config...");
    try {
      const response = await fetch(`${API_BASE_URL}/simulation/config`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ complexity, wip_limit: wipLimit, speed: simSpeed }), // Send complexity as dict
      });
      console.log("setConfig: Fetch request sent and response received.");
      const data = await response.json();
      if (response.ok) {
        console.log('setConfig: Config set successfully:', data);
      } else {
        console.error('setConfig: Failed to set config (server responded with error):', data);
      }
    } catch (error) {
      console.error('setConfig: Network error or problem sending request:', error);
    }
  }, [complexity, wipLimit, simSpeed]); // Depend on complexity (dict)

  const startSimulation = useCallback(async () => {
    console.log("startSimulation: Attempting to start simulation...");
    try {
      await setConfig();
      console.log("startSimulation: setConfig call finished.");

      const response = await fetch(`${API_BASE_URL}/simulation/start`, {
        method: 'POST',
      });
      const data = await response.json();
      if (response.ok) {
        console.log('startSimulation: Simulation started successfully:', data);
        setSimulationRunning(true);
      } else {
        console.error('startSimulation: Failed to start simulation (server responded with error):', data);
      }
    } catch (error) {
      console.error('startSimulation: Network error starting simulation or error from setConfig:', error);
    }
  }, [setConfig]);

  const stopSimulation = useCallback(async () => {
    console.log("Stopping simulation...");
    try {
      const response = await fetch(`${API_BASE_URL}/simulation/stop`, {
        method: 'POST',
      });
      const data = await response.json();
      if (response.ok) {
        console.log('Simulation stopped:', data);
        setSimulationRunning(false);
      } else {
        console.error('Failed to stop simulation:', data);
      }
    } catch (error) {
      console.error('Network error stopping simulation:', error);
    }
  }, []);

  const clearDashboard = useCallback(async () => {
    console.log("Clearing dashboard...");
    try {
      const response = await fetch(`${API_BASE_URL}/dashboard/clear`, {
        method: 'POST',
      });
      const data = await response.json();
      if (response.ok) {
        console.log('Dashboard cleared:', data);
        setDashboardData([]);
      } else {
        console.error('Failed to clear dashboard:', data);
      }
    } catch (error) {
      console.error('Network error clearing dashboard:', error);
    }
  }, []);

  useEffect(() => {
    let intervalId;
    const pollingInterval = 1000; // 1 second

    const fetchSimulationData = async () => {
      try {
        const boardResponse = await fetch(`${API_BASE_URL}/simulation/status`);
        const boardData = await boardResponse.json();
        if (boardResponse.ok) {
          setSimulationRunning(boardData.is_running);

          setLanes(prevLanes => {
            const newLanes = boardData.lanes;
            if (!prevLanes.length) return newLanes;

            const cardsToAnimateData = [];
            const oldCardsMap = new Map();
            prevLanes.forEach(lane => {
                lane.cards.forEach(card => {
                    oldCardsMap.set(card.id, card);
                });
            });

            for (const newLane of newLanes) {
                for (const newCard of newLane.cards) {
                    const oldCard = oldCardsMap.get(newCard.id);
                    if (oldCard && oldCard.col !== newCard.col) {
                        const cardElement = cardRefs.current.get(oldCard.id);
                        if (cardElement) {
                            cardsToAnimateData.push({
                                card: oldCard,
                                newLaneId: newCard.col,
                                startRect: cardElement.getBoundingClientRect()
                            });
                        } else {
                            console.warn(`[Gliding-Fix] Old element for card ${oldCard.id} (from lane ${oldCard.col} to ${newCard.col}) not found for animation. Skipping glide.`);
                        }
                    }
                }
            }

            // Immediately push animation data to a ref, then trigger re-render
            // to ensure FlyingCardAnimation components are mounted quickly.
            cardsToAnimateData.forEach(animData => {
                flyingAnimations.current.push({
                    id: `flying-${animData.card.id}-${Date.now()}`,
                    card: animData.card,
                    startRect: animData.startRect,
                    endRect: null // Will be updated by rAF
                });
            });
            // Force React to re-render to pick up new items in flyingAnimations.current
            // This is crucial when using a ref for an array that causes UI changes.
            flyingAnimations.current = [...flyingAnimations.current];


            // Schedule endRect calculation after React updates DOM for newLanes
            requestAnimationFrame(() => {
                flyingAnimations.current = flyingAnimations.current.map(anim => {
                    if (anim.startRect && anim.endRect === null) {
                        const newCardInDOM = cardRefs.current.get(anim.card.id);
                        if (newCardInDOM) {
                            const endRect = newCardInDOM.getBoundingClientRect();
                            return { ...anim, endRect: endRect };
                        } else {
                            console.warn(`[Gliding-Fix] Card ${anim.card.id} not found in new DOM position for endRect. Cancelling animation clone.`);
                            return null;
                        }
                    }
                    return anim;
                }).filter(Boolean); // Remove null entries (cancelled animations)
                flyingAnimations.current = [...flyingAnimations.current]; // Force re-render after updating endRects
            });

            return newLanes;
          });

        } else {
          console.error('Failed to fetch board state:', boardData);
          setSimulationRunning(false);
        }
      } catch (error) {
        console.error('Network error fetching board state:', error);
        setSimulationRunning(false);
      }

      try {
        const dashboardResponse = await fetch(`${API_BASE_URL}/dashboard/data`);
        const dashboardData = await dashboardResponse.json();
        if (dashboardResponse.ok) {
          setDashboardData(dashboardData.dashboard_entries || []);
        } else {
          console.error('Failed to fetch dashboard data:', dashboardData);
        }
      } catch (error) {
        console.error('Network error fetching dashboard data:', error);
      }
    };

    fetchSimulationData(); // Initial fetch
    intervalId = setInterval(fetchSimulationData, pollingInterval);
    return () => {
      clearInterval(intervalId);
      console.log('Polling interval cleared.');
    };
  }, [programmaticMoveCard]);


  // Handler for individual complexity input changes
   const handleComplexityChange = useCallback((colId, value) => {
    setComplexity(prev => ({
      ...prev,
      [colId]: parseInt(value) || 0 // Ensure it's an integer
    }));
  }, []);

  return (
    <div className="flex flex-col p-8 bg-gray-50 min-h-screen">
      {/* Simulation Controls (More Compact) */}
      <div className="mb-4 p-4 bg-white rounded-lg shadow-md max-w-full lg:max-w-5xl mx-auto flex flex-col sm:flex-row items-center justify-between gap-4">
        <h3 className="text-xl font-bold text-gray-800 flex-shrink-0">Controls</h3>

        {/* Global Parameters */}
        <div className="flex items-center gap-4 flex-grow justify-center">
            <div>
              <label className="block text-gray-700 text-sm font-bold mb-1">Overall WIP Limit:</label>
              <input
                type="number"
                value={wipLimit}
                onChange={(e) => setWipLimit(parseInt(e.target.value) || 0)}
                className="shadow appearance-none border rounded w-24 py-1 px-2 text-gray-700 leading-tight focus:outline-none focus:shadow-outline text-sm"
                min="0"
              />
            </div>
            <div>
              <label className="block text-gray-700 text-sm font-bold mb-1">Sim Speed (sec/step):</label>
              <input
                type="number"
                step="0.1"
                value={simSpeed}
                onChange={(e) => setSimSpeed(parseFloat(e.target.value) || 0.0)}
                className="shadow appearance-none border rounded w-24 py-1 px-2 text-gray-700 leading-tight focus:outline-none focus:shadow-outline text-sm"
                min="0.1"
              />
            </div>
        </div>

        {/* Action Buttons */}
        <div className="flex justify-center space-x-2 flex-shrink-0">
          <button
            onClick={startSimulation}
            disabled={!isReady || simulationRunning}
            className={`px-4 py-2 rounded-lg text-white font-bold text-sm transition-colors duration-200 ${
              !isReady || simulationRunning ? 'bg-gray-400 cursor-not-allowed' : 'bg-green-500 hover:bg-green-700'
            }`}
          >
            Start
          </button>
          <button
            onClick={stopSimulation}
            disabled={!isReady || !simulationRunning}
            className={`px-4 py-2 rounded-lg text-white font-bold text-sm transition-colors duration-200 ${
              !isReady || !simulationRunning ? 'bg-gray-400 cursor-not-allowed' : 'bg-red-500 hover:bg-red-700'
            }`}
          >
            Stop
          </button>
          <button
            onClick={clearDashboard}
            disabled={!isReady}
            className={`px-4 py-2 rounded-lg text-white font-bold text-sm transition-colors duration-200 ${
              !isReady ? 'bg-gray-400 cursor-not-allowed' : 'bg-yellow-500 hover:bg-yellow-700'
            }`}
          >
            Clear
          </button>
        </div>
      </div>

      {/* Kanban Board Display */}
      {/* Reduced padding, margin-right, and added fixed width for columns */}
      <div className="flex p-2 overflow-x-auto items-start bg-gray-100 rounded-lg shadow-inner flex-grow">
        {lanes.length === 0 ? (
          <p className="text-center text-gray-500 w-full p-10">
            No board data available. Start simulation to load cards.
          </p>
        ) : (
          lanes.map((lane) => (
            <div
              key={lane.id}
              ref={el => {
                if (el) laneRefs.current.set(lane.id, el);
                else laneRefs.current.delete(lane.id);
              }}
              // Column width styling, reduced padding/margin
              className="p-1 rounded-lg shadow-inner flex-shrink-0 mr-1 bg-gray-200 min-h-[400px] flex flex-col items-center"
              style={{ width: '130px' }} // Fixed smaller width for columns
            >
              <h2 className="text-sm font-semibold mb-1 text-gray-800 border-b border-gray-300 pb-1 text-center w-full">
                {lane.title}
                {/* Max WIP display for ALL columns */}
                {lane.max_wip_in_round !== undefined && (
                    <span className="text-xs text-gray-500 block">Max WIP: {lane.max_wip_in_round}</span>
                )}
                {/* NEW: Complexity input for "Schritt A/B/C" columns */}
                {(lane.id === 'lane-1' || lane.id === 'lane-3' || lane.id === 'lane-5') && (
                    <div className="mt-1">
                        <label className="block text-gray-700 text-xs font-bold mb-0">Komplexit√§t:</label>
                        <input
                            type="number"
                            value={complexity[parseInt(lane.id.split('-')[1])] || 1} // Get complexity from state, default to 1
                            onChange={(e) => handleComplexityChange(parseInt(lane.id.split('-')[1]), e.target.value)}
                            className="shadow appearance-none border rounded w-16 py-0 px-1 text-gray-700 leading-tight focus:outline-none focus:shadow-outline text-xs text-center"
                            min="1"
                        />
                    </div>
                )}
              </h2>
              <div className="flex flex-col items-center w-full mt-1 overflow-y-auto" style={{ maxHeight: 'calc(100% - 70px)' }}> {/* Adjusted height for overflow */}
                {lane.cards.map((card, index) => {
                  const isFlyingClone = flyingAnimations.current.some(anim => anim.card.id === card.id);
                  return (
                    <Card
                      key={card.id}
                      id={card.id}
                      text={card.birth_id ? `Karte ${card.birth_id}` : card.id} 
                      laneId={card.col}
                      index={index}
                      ref={el => {
                        if (el) cardRefs.current.set(card.id, el);
                        else cardRefs.current.delete(card.id);
                      }}
                      style={{
                        visibility: isFlyingClone ? 'hidden' : 'visible',
                        backgroundColor: card.is_red ? '#ca0633' : '#ffffff',
                        // NEW: Card width/padding styling
                        width: '90px', // Fixed smaller width for cards
                        height: '30px', // Fixed smaller height for cards
                        padding: '4px 6px', // Smaller padding
                        fontSize: '0.75rem', // Smaller font size
                        textAlign: 'center',
                        display: 'flex',
                        justifyContent: 'center',
                        alignItems: 'center',
                        marginBottom: '4px', // Smaller margin between cards
                      }}
                    />
                  );
                })}
              </div>
            </div>
          ))
        )}
      </div>

      {/* Dashboard Table (More Compact) */}
      <div className="mt-4 p-2 bg-white rounded-lg shadow-md max-w-full lg:max-w-5xl mx-auto text-sm">
        <h3 className="text-xl font-bold text-gray-800 mb-2 border-b pb-1">Dashboard</h3>
        {dashboardData.length === 0 ? (
          <p className="text-gray-500 text-xs p-1">Dashboard is empty. Run a simulation!</p>
        ) : (
          <div className="overflow-x-auto">
            <table className="min-w-full divide-y divide-gray-200">
              <thead className="bg-gray-50">
                <tr>
                  <th className="px-1 py-1 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Round</th>
                  <th className="px-1 py-1 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">WiP Limit</th>
                  <th className="px-1 py-1 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Cycle Time (Red Card)</th>
                  <th className="px-1 py-1 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Flow Efficiency</th>
                  <th className="px-1 py-1 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">In Progress</th>
                  <th className="px-1 py-1 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Done</th>
                  <th className="px-1 py-1 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Throughput</th>
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-gray-200">
                {dashboardData.map((entry, index) => (
                  <tr key={index}>
                    <td className="px-1 py-1 whitespace-nowrap text-xs text-gray-900">{entry.round}</td>
                    <td className="px-1 py-1 whitespace-nowrap text-xs text-gray-900">{entry.wip_limit}</td>
                    <td className="px-1 py-1 whitespace-nowrap text-xs text-gray-900">{entry.red_card_cycle_time}</td>
                    <td className="px-1 py-1 whitespace-nowrap text-xs text-gray-900">{entry.flow_efficiency}</td>
                    <td className="px-1 py-1 whitespace-nowrap text-xs text-gray-900">{entry.in_progress}</td>
                    <td className="px-1 py-1 whitespace-nowrap text-xs text-gray-900">{entry.done}</td>
                    <td className="px-1 py-1 whitespace-nowrap text-xs text-gray-900">{entry.throughput}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>

      {/* Render flying animations on top of everything */}
      {flyingAnimations.current.map(anim => anim.startRect && (
        <FlyingCardAnimation
          key={anim.id}
          card={anim.card}
          startRect={anim.startRect}
          endRect={anim.endRect}
          onAnimationEnd={handleAnimationEnd}
        />
      ))}
    </div>
  );
};

export default Board;
