// src/components/Board.js
import React, { useState, useCallback, useRef, useEffect } from 'react';
import Lane from './Lane';
import Card from './Card';
import FlyingCardAnimation from './FlyingCardAnimation';

// --- API Configuration ---
// IMPORTANT: Use your FastAPI backend URL.
// If running locally, it's usually http://127.0.0.1:8000
const API_BASE_URL = 'http://127.0.0.1:8000';

const Board = () => {
  // --- React State for Board and Simulation Controls ---
  const [lanes, setLanes] = useState([]); // Initial empty lanes, will be populated from backend
  const [dashboardData, setDashboardData] = useState([]); // For the dashboard table

  // Simulation Parameters state (for user input)
  const [complexity, setComplexity] = useState(1);
  const [wipLimit, setWipLimit] = useState(5);
  const [simSpeed, setSimSpeed] = useState(1.0); // In seconds per step

  const [simulationRunning, setSimulationRunning] = useState(false); // Tracks backend status
  const [currentRound, setCurrentRound] = useState(0); // Tracks backend round

  const [flyingAnimations, setFlyingAnimations] = useState([]);

  // Refs for card elements for gliding animation
  const cardRefs = useRef(new Map());
  const laneRefs = useRef(new Map());

  // Board readiness for first render (from previous step)
  const [isReady, setIsReady] = useState(false);
  useEffect(() => {
    const timeoutId = setTimeout(() => {
      setIsReady(true);
      console.log('Board component and initial refs are likely ready.');
    }, 100);
    return () => clearTimeout(timeoutId);
  }, []);

  // --- Helper to get a card by its ID from current React lanes state ---
  const getCardById = useCallback((cardId) => {
    for (const lane of lanes) {
      const card = lane.cards.find(c => c.id === cardId);
      if (card) return card;
    }
    return null;
  }, [lanes]);

  // --- Programmatic Card Move (Gliding) ---
  const programmaticMoveCard = useCallback((cardId, targetLaneId) => {
    const cardElement = cardRefs.current.get(cardId);
    const targetLaneElement = laneRefs.current.get(targetLaneId);

    if (!cardElement || !targetLaneElement) {
      console.warn(`[Gliding] Could not find element for card ${cardId} or lane ${targetLaneId}. Cannot animate.`);
      return;
    }

    const startRect = cardElement.getBoundingClientRect();
    const currentCardData = getCardById(cardId);

    // If cardData is null (e.g. card moved off board), don't animate
    if (!currentCardData) {
        console.warn(`[Gliding] Card ${cardId} not found in current state, skipping animation.`);
        return;
    }

    setFlyingAnimations(prev => [
      ...prev,
      {
        id: `flying-${cardId}-${Date.now()}`, // Unique ID for each animation instance
        card: currentCardData,
        startRect: startRect,
      }
    ]);

    // Schedule getting endRect after React re-renders the actual card
    requestAnimationFrame(() => {
      const endCardElement = cardRefs.current.get(cardId);
      if (endCardElement) {
        const endRect = endCardElement.getBoundingClientRect();
        setFlyingAnimations(prev => prev.map(anim =>
          anim.card.id === cardId && !anim.endRect
            ? { ...anim, endRect: endRect }
            : anim
        ));
      } else {
        // If card disappeared (e.g., moved to Done and removed from board), clear animation
        setFlyingAnimations(prev => prev.filter(anim => anim.card.id !== cardId));
      }
    });

  }, [getCardById]);

  // Callback for when a flying animation ends
  const handleAnimationEnd = useCallback((animatedCardId) => {
    setFlyingAnimations(prev => prev.filter(anim => anim.card.id !== animatedCardId));
  }, []);

  // --- API Calls to Python Backend ---

  const setConfig = useCallback(async () => {
    console.log("Setting simulation config...");
    try {
      const response = await fetch(`${API_BASE_URL}/simulation/config`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ complexity, wip_limit: wipLimit, speed: simSpeed }),
      });
      const data = await response.json();
      if (response.ok) {
        console.log('Config set:', data);
        alert('Simulation parameters updated!');
      } else {
        console.error('Failed to set config:', data);
        alert(`Error setting config: ${data.detail || 'Unknown error'}`);
      }
    } catch (error) {
      console.error('Network error setting config:', error);
      alert('Network error: Could not connect to backend to set config.');
    }
  }, [complexity, wipLimit, simSpeed]);


  const startSimulation = useCallback(async () => {
    console.log("Starting simulation...");
    try {
      // Ensure parameters are set first
      await setConfig();

      const response = await fetch(`${API_BASE_URL}/simulation/start`, {
        method: 'POST',
      });
      const data = await response.json();
      if (response.ok) {
        console.log('Simulation started:', data);
        setSimulationRunning(true);
        alert('Simulation started!');
      } else {
        console.error('Failed to start simulation:', data);
        alert(`Error starting simulation: ${data.detail || 'Unknown error'}`);
      }
    } catch (error) {
      console.error('Network error starting simulation:', error);
      alert('Network error: Could not connect to backend to start simulation.');
    }
  }, [setConfig]); // Depend on setConfig

  const stopSimulation = useCallback(async () => {
    console.log("Stopping simulation...");
    try {
      const response = await fetch(`${API_BASE_URL}/simulation/stop`, {
        method: 'POST',
      });
      const data = await response.json();
      if (response.ok) {
        console.log('Simulation stopped:', data);
        setSimulationRunning(false);
        alert('Simulation stopped!');
      } else {
        console.error('Failed to stop simulation:', data);
        alert(`Error stopping simulation: ${data.detail || 'Unknown error'}`);
      }
    } catch (error) {
      console.error('Network error stopping simulation:', error);
      alert('Network error: Could not connect to backend to stop simulation.');
    }
  }, []);

  const clearDashboard = useCallback(async () => {
    console.log("Clearing dashboard...");
    try {
      const response = await fetch(`${API_BASE_URL}/dashboard/clear`, {
        method: 'POST',
      });
      const data = await response.json();
      if (response.ok) {
        console.log('Dashboard cleared:', data);
        setDashboardData([]); // Clear frontend dashboard data instantly
        alert('Dashboard cleared!');
      } else {
        console.error('Failed to clear dashboard:', data);
        alert(`Error clearing dashboard: ${data.detail || 'Unknown error'}`);
      }
    } catch (error) {
      console.error('Network error clearing dashboard:', error);
      alert('Network error: Could not connect to backend to clear dashboard.');
    }
  }, []);

  // --- Polling for Board State and Dashboard Data ---
  useEffect(() => {
    let intervalId;

    const fetchSimulationData = async () => {
      // Fetch board state
      try {
        const boardResponse = await fetch(`${API_BASE_URL}/simulation/status`);
        const boardData = await boardResponse.json();
        if (boardResponse.ok) {
          // console.log('Fetched board state:', boardData);
          setSimulationRunning(boardData.is_running);
          // Only update currentRound if the backend sends it
          // if (boardData.current_round !== undefined) {
          //   setCurrentRound(boardData.current_round);
          // }

          // --- Compare and trigger gliding animation ---
          // This is the core logic for making cards glide based on backend updates
          setLanes(prevLanes => {
            const newLanes = boardData.lanes;
            if (!prevLanes.length) return newLanes; // First load, no animation needed

            const movedCardIds = new Set();
            for (const newLane of newLanes) {
                for (const newCard of newLane.cards) {
                    const oldLane = prevLanes.find(l => l.id === newCard.laneId);
                    // Check if card is in a new lane or disappeared
                    if (!oldLane || !oldLane.cards.some(c => c.id === newCard.id)) {
                        // Card appeared in newLane, check if it was present somewhere else before
                        const wasPresentBefore = prevLanes.some(l => l.cards.some(c => c.id === newCard.id));
                        if (wasPresentBefore) {
                            movedCardIds.add(newCard.id);
                        }
                    }
                }
            }

            // Trigger animation for identified moved cards
            movedCardIds.forEach(cardId => {
                const newCard = newLanes.flatMap(l => l.cards).find(c => c.id === cardId);
                if (newCard) {
                    programmaticMoveCard(cardId, newCard.laneId);
                }
            });

            return newLanes; // Update React state with the new board state
          });

        } else {
          console.error('Failed to fetch board state:', boardData);
          setSimulationRunning(false);
        }
      } catch (error) {
        console.error('Network error fetching board state:', error);
        setSimulationRunning(false); // Assume simulation stopped if network fails
      }

      // Fetch dashboard data
      try {
        const dashboardResponse = await fetch(`${API_BASE_URL}/dashboard/data`);
        const dashboardData = await dashboardResponse.json();
        if (dashboardResponse.ok) {
          // console.log('Fetched dashboard data:', dashboardData);
          setDashboardData(dashboardData.dashboard_entries || []); // Ensure it's an array
        } else {
          console.error('Failed to fetch dashboard data:', dashboardData);
        }
      } catch (error) {
        console.error('Network error fetching dashboard data:', error);
      }
    };

    // Start polling if simulationRunning is true (or initially to get state)
    // The interval speed is fixed here. Backend speed just controls backend ticking.
    const pollingInterval = 1500; // Poll every 500ms (adjust as needed)

    // Initial fetch when component mounts or simulationRunning status changes
    fetchSimulationData();

    // Set up interval only if simulation is meant to be running or we want continuous updates
    intervalId = setInterval(fetchSimulationData, pollingInterval);

    // Clear interval on component unmount or when simulation explicitly stops
    return () => {
      clearInterval(intervalId);
      console.log('Polling interval cleared.');
    };
  }, [programmaticMoveCard]); // Re-run effect if programmaticMoveCard callback changes (unlikely)


  // --- Render UI ---
  return (
    <div className="flex flex-col p-8 bg-gray-50 min-h-screen">
      {/* Simulation Controls */}
      <div className="mb-8 p-6 bg-white rounded-lg shadow-md max-w-full lg:max-w-4xl mx-auto">
        <h3 className="text-2xl font-bold text-gray-800 mb-4 border-b pb-2">Simulation Controls</h3>
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
          <div>
            <label className="block text-gray-700 text-sm font-bold mb-2">Complexity:</label>
            <input
              type="number"
              value={complexity}
              onChange={(e) => setComplexity(parseInt(e.target.value) || 0)}
              className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
              min="1"
            />
          </div>
          <div>
            <label className="block text-gray-700 text-sm font-bold mb-2">WIP Limit:</label>
            <input
              type="number"
              value={wipLimit}
              onChange={(e) => setWipLimit(parseInt(e.target.value) || 0)}
              className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
              min="0"
            />
          </div>
          <div>
            <label className="block text-gray-700 text-sm font-bold mb-2">Sim Speed (sec/step):</label>
            <input
              type="number"
              step="0.1"
              value={simSpeed}
              onChange={(e) => setSimSpeed(parseFloat(e.target.value) || 0.0)}
              className="shadow appearance-none border rounded w-full py-2 px-3 text-gray-700 leading-tight focus:outline-none focus:shadow-outline"
              min="0.1"
            />
          </div>
        </div>
        <div className="flex justify-center space-x-4">
          <button
            onClick={startSimulation}
            disabled={!isReady || simulationRunning}
            className={`px-6 py-3 rounded-lg text-white font-bold transition-colors duration-200 ${
              !isReady || simulationRunning ? 'bg-gray-400 cursor-not-allowed' : 'bg-green-500 hover:bg-green-700'
            }`}
          >
            Start Simulation
          </button>
          <button
            onClick={stopSimulation}
            disabled={!isReady || !simulationRunning}
            className={`px-6 py-3 rounded-lg text-white font-bold transition-colors duration-200 ${
              !isReady || !simulationRunning ? 'bg-gray-400 cursor-not-allowed' : 'bg-red-500 hover:bg-red-700'
            }`}
          >
            Stop Simulation
          </button>
          <button
            onClick={clearDashboard}
            disabled={!isReady}
            className={`px-6 py-3 rounded-lg text-white font-bold transition-colors duration-200 ${
              !isReady ? 'bg-gray-400 cursor-not-allowed' : 'bg-yellow-500 hover:bg-yellow-700'
            }`}
          >
            Clear Dashboard
          </button>
        </div>
      </div>

      {/* Kanban Board Display */}
      <div className="flex p-4 overflow-x-auto items-start bg-gray-100 rounded-lg shadow-inner flex-grow">
        {lanes.length === 0 ? (
          <p className="text-center text-gray-500 w-full p-10">
            No board data available. Start simulation to load cards.
          </p>
        ) : (
          lanes.map((lane) => (
            <div
              key={lane.id}
              ref={el => {
                if (el) laneRefs.current.set(lane.id, el);
                else laneRefs.current.delete(lane.id);
              }}
              className="w-80 p-4 rounded-lg shadow-inner flex-shrink-0 mr-4 bg-gray-200 min-h-[400px]"
            >
              <h2 className="text-xl font-semibold mb-4 text-gray-800 border-b border-gray-300 pb-2">{lane.title} <span className="text-sm text-gray-500">WIP:{lane.wip_limit}</span></h2>
              {lane.cards.map((card, index) => {
                const isFlyingClone = flyingAnimations.some(anim => anim.card.id === card.id && anim.endRect);
                return (
                  <Card
                    key={card.id}
                    id={card.id}
                    text={card.birth_id ? `Card ${card.birth_id}` : card.id} /* Use birth_id for display */
                    laneId={card.col} // Use card.col as laneId
                    index={index}
                    ref={el => {
                      if (el) cardRefs.current.set(card.id, el);
                      else cardRefs.current.delete(card.id);
                    }}
                    style={{ visibility: isFlyingClone ? 'hidden' : 'visible', backgroundColor: card.is_red ? '#ca0633' : '#ffffff' }}
                  />
                );
              })}
            </div>
          ))
        )}
      </div>

      {/* Dashboard Table */}
      <div className="mt-8 p-6 bg-white rounded-lg shadow-md max-w-full lg:max-w-4xl mx-auto">
        <h3 className="text-2xl font-bold text-gray-800 mb-4 border-b pb-2">Dashboard</h3>
        {dashboardData.length === 0 ? (
          <p className="text-gray-500">Dashboard is empty. Run a simulation!</p>
        ) : (
          <div className="overflow-x-auto">
            <table className="min-w-full divide-y divide-gray-200">
              <thead className="bg-gray-50">
                <tr>
                  <th className="px-3 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Round</th>
                  <th className="px-3 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">WIP Limit</th>
                  <th className="px-3 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Cycle Time (Red Card)</th>
                  <th className="px-3 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Flow Efficiency</th>
                  <th className="px-3 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">In Progress</th>
                  <th className="px-3 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Done</th>
                  <th className="px-3 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Throughput</th>
                </tr>
              </thead>
              <tbody className="bg-white divide-y divide-gray-200">
                {dashboardData.map((entry, index) => (
                  <tr key={index}> {/* Using index as key is okay for static lists, but prefer unique IDs if available */}
                    <td className="px-3 py-2 whitespace-nowrap text-sm text-gray-900">{entry.round}</td>
                    <td className="px-3 py-2 whitespace-nowrap text-sm text-gray-900">{entry.wip_limit}</td>
                    <td className="px-3 py-2 whitespace-nowrap text-sm text-gray-900">{entry.red_card_cycle_time}</td>
                    <td className="px-3 py-2 whitespace-nowrap text-sm text-gray-900">{entry.flow_efficiency}</td>
                    <td className="px-3 py-2 whitespace-nowrap text-sm text-gray-900">{entry.in_progress}</td>
                    <td className="px-3 py-2 whitespace-nowrap text-sm text-gray-900">{entry.done}</td>
                    <td className="px-3 py-2 whitespace-nowrap text-sm text-gray-900">{entry.throughput}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>

      {/* Render flying animations on top of everything */}
      {flyingAnimations.map(anim => anim.startRect && anim.endRect && (
        <FlyingCardAnimation
          key={anim.id}
          card={anim.card}
          startRect={anim.startRect}
          endRect={anim.endRect}
          onAnimationEnd={handleAnimationEnd}
        />
      ))}
    </div>
  );
};

export default Board;
